// Alvi contains all Tracers & Data objects.
// Pass AVTM as the second argument when initializing Tracers.
var chart = new Alvi.ChartTracer('',AVTM);
var tracer = new Alvi.Array1DTracer('',AVTM).attach(chart);
var logger = new Alvi.LogTracer('',AVTM);
var D = Alvi.Array1D.random(25, -12, 37);
tracer._setData(D);
//stop <- put data setup before this, and algorithm visualization code after
logger._print('original array = [' + D.join(', ') + ']');
var N = D.length;
var swapped;
do {
    swapped = false;
    tracer._select(N - 1)._wait();
    for (var i = 1; i < N; i++) {
        tracer._select(i)._wait();
        if (D[i - 1] > D[i]) {
            logger._print('swap ' + D[i - 1] + ' and ' + D[i]);
            var temp = D[i - 1];
            D[i - 1] = D[i];
            D[i] = temp;
            swapped = true;
            tracer._notify(i - 1, D[i - 1])._notify(i, D[i])._wait();
            tracer._denotify(i - 1)._denotify(i);
        }
        tracer._deselect(i);
    }
    tracer._deselect(N - 1);
    N--;
} while (swapped);
logger._print('sorted array = [' + D.join(', ') + ']');

/* You are given 4 variables to work with:
data - your data as an array of objects
element - the d3 selection of an svg, run d3 methods on it
width - use the preset width value
height - use the preset height value
*/

// all data is imported as Strings, so convert to types as needed:
var data = data.map(function(x) {
  x.frequency = +x.frequency;
  return x;
});

// the code below generates a bar graph based on the data
// for more examples visit: academic.systems/bengine-d3
var g = element.append("g")
    .attr("transform", "translate(40,20)");

var x = d3.scaleBand().rangeRound([0, width]).padding(0.1),
    y = d3.scaleLinear().rangeRound([height, 0]);

x.domain(data.map(function(d) { return d.letter; }));
y.domain([0, d3.max(data, function(d) { return d.frequency; })]);

g.append("g")
    .attr("class", "axis axis--x")
    .attr("transform", "translate(0," + height + ")")
    .call(d3.axisBottom(x));

g.append("g")
    .attr("class", "axis axis--y")
    .call(d3.axisLeft(y).ticks(10, "%"))
  .append("text")
    .attr("transform", "rotate(-90)")
    .attr("y", 6)
    .attr("dy", "0.71em")
    .attr("text-anchor", "end")
    .text("Frequency");

g.selectAll(".bar")
  .data(data)
  .enter().append("rect")
    .attr("class", "bar")
    .attr("x", function(d) { return x(d.letter); })
    .attr("y", function(d) { return y(d.frequency); })
    .attr("width", x.bandwidth())
    .attr("height", function(d) { return height - y(d.frequency); });

// define any css your svg needs
renderCSS(`
.bar {
  fill: steelblue;
}

.bar:hover {
  fill: brown;
}

.axis--x path {
  display: none;
}
`);
